$(document).ready(function(){
	decode();
});

/**
 * Decodes the HTML that must be printed as decoded entities in the report.
 * The encoded entities contains the Dextra Selenium Exceptions generated by the test cases.
 */
function decode() {
	var tds = $('body').find('td');
	$.each(tds, function(i, td){
		var content = $(td).html();
		var notExistDextraExceptions = content.indexOf('DextraSeleniumException') < 0 && content.indexOf('NoErrorOccurredException') < 0;
		if(content.indexOf('&lt;') >= 0 && notExistDextraExceptions) {
			var decoded = $("<div/>").html(content).text();
			console.debug(decoded);
			$(td).html(decoded);
		}
	});
};

